fn main(){
    sumar(32, 65);
    restar(32, 65);
    multiplicar(32, 65);
    division(32, 65);
}
fn sumar(num: i32, num1: i32){
    let operacion = num + num1;
    println!("La suma de {num} + {num1} es {operacion}");
}
fn restar(num: i32, num1: i32){
    let operacion = num - num1;
    println!("La resta de {num} - {num1} es {operacion}");
    let resultado = num1 - num;
    println!("La resta de {num1} - {num} es {resultado}");
}
fn multiplicar(num: i32, num1: i32){
    let operacion = num * num1;
    println!("La multiplicacion de {num} * {num1} es {operacion}");
}
fn division(num: i32, num1: i32){
    let operacion = num / num1;
    println!("La division de {num} / {num1} es {operacion}");
    let resultado = num1 / num;
    println!("La division de {num1} / {num} es {resultado}");
}